import { createAsyncThunk, createSlice } from '@reduxjs/toolkit';
import { useSelector } from 'react-redux';
import axios from 'axios';

import * as APIS from "../utils/service";
import { API_URL } from '../libs/apiUrl';

/**
 * 유저 정보 가져오기
 */
export const loadUserInfo = createAsyncThunk(
    "requestUser/loadInfo",
    async () => {

        const requestData = () => {
            return new Promise((resolve, reject) => {
                APIS.postData(API_URL.user.info, {}).then(result => {
                    resolve(result.data)
                }).catch(e => {
                    reject(e)
                })
            })
        }

        return await requestData()
    }
);

const user = createSlice({
    name: 'userinfo',
    initialState: {
        loadStatus: "wait",
        userInfo: {
            idx: 0,
            type: "",
            id: "",
            email: "",
            level: 0,
            name: "",
            hp: "",
            sex: "",
            profile: null,
            point: 0,
            agree_marketing: 0,
            penalty: null,
            certification: null,
            login_dt: "",
            create_dt: "",
            update_dt: "",
            delete_dt: null,
            delete_reason: null,
            penalty_check: 0,
            car_owner:"",
            car_reg_no:"",
        },
        boardCount: {
            truck_count: 0,
            jeeip_count: 0,
            recruit_count: 0,
            job_count: 0,
            consulting_count: 0
        }
    },
    extraReducers: (builder) => {
        builder.addCase(loadUserInfo.pending, (state) => {
            state.loadStatus = "loading"
        });
        builder.addCase(loadUserInfo.fulfilled, (state, { payload }) => {
            state.userInfo = payload
        });
        builder.addCase(loadUserInfo.rejected, (state, action) => {
            state.loadStatus = "fail"
        });
    },
    reducers: {
        setUserInfo(state, action) {
            state.userInfo = action.payload.userInfo
        },
        setUserBoard(state, action) {
            state.boardCount = {
                truck_count: action.payload.truck_count > 0 ? action.payload.truck_count : 0,
                jeeip_count: action.payload.jeeip_count > 0 ? action.payload.jeeip_count : 0,
                recruit_count: action.payload.recruit_count > 0 ? action.payload.recruit_count : 0,
                job_count: action.payload.job_count > 0 ? action.payload.job_count : 0,
                consulting_count: action.payload.consulting_count > 0 ? action.payload.consulting_count : 0,
            }
        },
        setUserInfoTest(state, action) {
            state.boardCount = {
                truck_count: state.boardCount.truck_count + action.payload.value,
                jeeip_count: state.boardCount.jeeip_count + action.payload.value,
                recruit_count: state.boardCount.recruit_count + action.payload.value,
                job_count: state.boardCount.job_count + action.payload.value,
            }

            // console.log(state.boardCount.truck_count)
        }
    }

});



export const {
    setUserInfo,
    setUserBoard,
    setUserInfoTest,
} = user.actions;

export const selectUserInfo = (s) => s.userReducer

export default user.reducer;
